routine.definition:


DECLARE
   r public.sisepdesc_categoria%ROWTYPE;



BEGIN
  -- init vars

  FOR r IN SELECT * FROM public.sisepdesc_categoria
  LOOP

    IF r.tipo='simple' or r.tipo='dupla' THEN

      EXECUTE 'UPDATE '||r.nombre_tabla||' set state=false where id_pre_registro_persona='||id_pre_registro_persona;

    END IF;

  END LOOP;

  RETURN 1::text;
END;
